import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpDateTimePickerMulti,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

import Pop_C0807002 from './Pop_C0807002';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                title="교정관리 출력"
                callbackFunction={this.createDialogOpen}
                dialogButton="출력"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <Pop_C0807002/>
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                출력
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="800px"
                height="auto"
            >
                <React.Fragment>
                    <Formsy>
                        <div style={{width:'100%'}}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                <tr>
                                    <th className="left" width="120">구분</th>
                                    <td className="left">
                                        <OwpSelectField
                                            className="w-full"
                                            id="b"
                                            name="b"
                                            options={[
                                                { label: '실적', value: '1' },
                                                { label: '실적', value: '2' },
                                            ]}
                                            value={'1'}
                                        />
                                    </td>
                                    <th className="left" width="120">기간</th>
                                    <td className="left">
                                        <OwpDateTimePickerMulti
                                            initNow
                                            labels={{ start: 'start', end: 'end' }}
                                            inputProps={{ start: { style: { width: 160 } }, end: { style: { width: 160 } } }}
                                            
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <th className="left" width="120">머리말 입력</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                        />
                                    </td>
                                    <th className="left" width="120">꼬리말 입력</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                        />
                                    </td>
                                </tr>
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
