/* eslint-disable array-callback-return */
import React, { Component } from 'react';
import {
    OwpButton,
    OwpSearchDateField,
    OwpSearchSelectField,
    OwpSearchTextField,
    OwpTextField,
    OwpTreeGrid,
} from 'owp/wrapper';
import Formsy from 'formsy-react';
import * as TGEvent from 'owp/TGEvent';
import moment from 'moment';
import { query } from 'owp/api';
import { setGridData, setRowAttribute } from 'main/GridUtill';

const FPNUNIT = 'OWP_MBR_PROCESS_MAIN.FPNUNIT';
const KPPCPP_ACTIVE = 'OWP_MBR_PROCESS_MAIN.KPPCPP_ACTIVE';
const TARGETMIN_ACTIVE = 'OWP_MBR_PROCESS_MAIN.TARGETMIN_ACTIVE';
const TARGETMAX_ACTIVE = 'OWP_MBR_PROCESS_MAIN.TARGETMAX_ACTIVE';
const TOLARANCE_ACTIVE = 'OWP_MBR_PROCESS_MAIN.TOLARANCE_ACTIVE';
const SP_ACTIVE = 'OWP_MBR_PROCESS_MAIN.SP_ACTIVE';
const PV_ACTIVE = 'OWP_MBR_PROCESS_MAIN.PV_ACTIVE';
const REPORT_ACTIVE = 'OWP_MBR_PROCESS_MAIN.REPORT_ACTIVE';

class Page extends Component {
    constructor(props) {
        super(props);
        //TODO:load this from parent
        this.state = {
            UNITPROCESSID: 140,
            MAIN_CONFIG: '',
        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
        this.loadActiveGrid();
    }
    //날짜 디폴드값 설정 함수

    loadActiveGrid = () => {
        //TODO:using another service
        query({
            url: '/listOWP_MBR_PROCESS_MAINActiveByUnitProcessID',
            params: {
                jsondata: JSON.stringify({
                    'OWP_MBR_PROCESS_MAIN.UNITPROCESSID': this.state.UNITPROCESSID + '',
                }),
            },
        })
            .then((res) => {
                res[0][FPNUNIT] = 'TR';
                res[1][FPNUNIT] = 'TJ';
                res[2][FPNUNIT] = 'RPM';
                res[3][FPNUNIT] = 'P';
                // this.setState((state) => {
                //     return {
                //         ...state,
                //         MAIN_CONFIG: res,
                //     };
                // });
                if (res && res.length > 0) {
                    setGridData('C0202012', res);
                    this.canEdit();
                    this.getDataFromGrid();
                }
            })
            .catch((err) => {
                console.log(err);
            });
    };

    getDataFromGrid = () => {
        const G = TGEvent.getGridByID('C0202012');
        const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
        let arrData = [];
        _rows.some((row) => {
            let rowData = {
                'OWP_MBR_PROCESS_MAIN.FPNUNIT': row[FPNUNIT],
                'OWP_MBR_PROCESS_MAIN.KPPCPP_ACTIVE': row[KPPCPP_ACTIVE],
                'OWP_MBR_PROCESS_MAIN.TARGETMIN_ACTIVE': row[TARGETMIN_ACTIVE],
                'OWP_MBR_PROCESS_MAIN.TARGETMAX_ACTIVE': row[TARGETMAX_ACTIVE],
                'OWP_MBR_PROCESS_MAIN.TOLARANCE_ACTIVE': row[TOLARANCE_ACTIVE],
                'OWP_MBR_PROCESS_MAIN.SP_ACTIVE': row[SP_ACTIVE],
                'OWP_MBR_PROCESS_MAIN.PV_ACTIVE': row[PV_ACTIVE],
                'OWP_MBR_PROCESS_MAIN.REPORT_ACTIVE': row[REPORT_ACTIVE],
            };
            // arrData.push(JSON.stringify(rowData));
            arrData.push(rowData);
        });
        console.log(JSON.stringify(arrData));
    };

    canEdit = () => {
        const G = TGEvent.getGridByID('C0202012');
        const _rows = Object.values(G['Rows']).filter((item) => item.Kind == 'Data');
        const EDIT_COLUMNS = [
            KPPCPP_ACTIVE,
            TARGETMIN_ACTIVE,
            TARGETMAX_ACTIVE,
            TOLARANCE_ACTIVE,
            SP_ACTIVE,
            PV_ACTIVE,
            REPORT_ACTIVE,
        ];
        _rows.some((row) => {
            EDIT_COLUMNS.some((targetColumnName) => {
                setRowAttribute('C0202012', row, targetColumnName, 'BoolIcon', '4', 1);
            });
        });
    };

    render() {
        return (
            <React.Fragment>
                <div style={{ width: '100%' }}>
                    <div className="row">
                        <div className="col-md-12" style={{ padding: '0' }}>
                            <OwpTreeGrid
                                id="C0202012"
                                LayoutUrl="assets/data/C0202012Def.xml"
                                width="100%"
                                // height="21vh"
                            />
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default Page;
