import {
    OwpTextField,
    OwpButton,
    OwpSelectField,
    OwpDialog,
    OwpMessage,
    OwpTreeGrid,
    OwpSearchDateTimePickerMulti,
    OwpSearchSelectField,
    OwpSearchTextField,
} from 'owp/wrapper';
import React, { Component } from 'react';
import Formsy from 'formsy-react';
import { mutate } from 'owp/api';
import * as TGEvent from 'owp/TGEvent';

class DialogReweighing extends Component {
    constructor(props) {
        super(props);
        this.owpDialog = React.createRef();
        this.clickCreate = this.clickCreate.bind(this);
        this.onSave = this.onSave.bind(this);
        this.state = {
            FLAG: 'Y',
        };
    }

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    

    // 등록 화면 닫기
    createDialogClose = () => {
        this.setState({ createDialogOpen: false });
        this.owpDialog.handleClose();
    };

    // 등록 확인 화면 열기
    createCheckOpen = () => {

        this.setState({ createCheckOpen: true });
        this.setState({ popMessage: '저장하시겠습니까?' });
    };

    // 등록 확인 화면 닫기
    createCheckClose = () => {
        this.setState({ createCheckOpen: false });
    };

    onSave = async (type) => {
        const data = {
            cudtype: 'create',
            
        };

        const result = await mutate({
            url: '/createIpxCommoncode',
            data,
        })
            
    };

    clickCreate() {
        this.onSave('START');
    }

    render() {
        return (
            <OwpDialog
                title="작업표준관리(SOP) 상세수정"
                callbackFunction={this.createDialogOpen}
                dialogButton="세부수정(임시버튼)"
                dialogActions={
                    <React.Fragment>
                        <div className="w-full center">
                            <OwpButton color="secondary" variant="contained" onClick={this.createCheckOpen}>
                                수정
                            </OwpButton>
                            <OwpButton color="secondary" variant="outlined" onClick={this.createDialogClose}>
                                취소
                            </OwpButton>
                        </div>
                    </React.Fragment>
                }
                innerRef={(ref) => (this.owpDialog = ref)}
                width="800px"
                height="auto"
            >
                <React.Fragment>
                    <Formsy>
                        <div style={{width:'100%'}}>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                <tr>
                                    <th className="left" width="120">분류 및 SOP 명</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                            value={'국소 배기 장치 관리 절차'}
                                        />
                                    </td>
                                    <th className="left" width="120">문서번호</th>
                                    <td className="left">
                                        <OwpTextField
                                            style={{width:'100%'}}
                                            name="c"
                                            value={'PS02226'}
                                        />
                                    </td>
                                </tr>
                                <tr>
                                    <td className="left" colspan="4">
                                        <textarea
                                            className="t_textarea"
                                            style={{height:'500px', whiteSpace:'pre-line'}}>
 국소 배기 장치 관리 절차

1      목적 (Purpose) 본 절차는 SK 바이오텍 Plant 내에 설치되어 사용되고 있는 국소 배기 장치(Local Exhaust Ventilation System, 이하 LEV) 관리를 위한  표준 작업 절차를 제공한다.
2      적용범위 (Scope)
2.1    본 절차는 세종특별자치시 연동면 명학산단로 80에 위치한 SK바이오텍 세종 Plant 에서 설치되어 사용되고 있는 국소 배기 장치(LEV) 관리에 적용된다.

3      책임사항 (Responsibility)
3.1    제조운전조원
        3.1.1  국소 배기 장치 관리 절차의 숙지
        3.1.2  국소 배기 장치 관리 및 세척 실시

3.2    설비관리책임자
        3.2.1  국소 배기 장치 점검
        3.2.2  국소 배기 장치 이상 발생시 조치
                                               
                                        </textarea>
                                    </td>
                                </tr>
                                
                                </tbody>
                            </table>
                        </div>
                    </Formsy>
                </React.Fragment>
            </OwpDialog>
        );
    }
}

export default DialogReweighing;
