import React, { Component } from "react";
import { OwpButton, OwpSearchDateField, OwpSearchSelectField, OwpSearchTextField, OwpTextField, OwpSelectField } from 'owp/wrapper';
import Formsy from "formsy-react";
import * as TGEvent from 'owp/TGEvent';
import moment from "moment";
import { mutate, query } from 'owp/api';

const KOREAN_FORMAT_DATE = 'YYYY-MM-DD';
class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
    }
    /**
     * Owp에서 사용하는 인풋에 대해 모두 사용할 수 있는 change 이벤트 리시버
     * @param {Event} event 발생한 이벤트 객체
     * @param {Function} callback 콜백 함수. 인풋의 name, value를 인수로 전달함.
     */
    componentDidMount() {
        const _showApprovalDialog = this.showApprovalDialog;
        this.loadData();
    }

    //날짜 디폴드값 설정 함수

    loadData = async () => {
        query({
            // url: "loadOWP_MBRCover/" + this.props.MULTYLANGSEQ,
            url: "loadOWP_MBRCover/1",
        }).then(res => {
            for (let key in res) {
                console.log(key);
                let stateObj = {};
                stateObj[key] = res[key];
                this.setState(stateObj);
            }
            console.log(this.state);
        })
        .catch(error =>{
           console.error(error);
        })
    };

    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    };

    render() {
        return (
            <React.Fragment>
                <Formsy>
                    <div style={{ width: '100%' }}>
                        <div>
                            <div className="b mgt20">Document Information and Document registration approval information</div>
                            <table className="table table-bordered table-advance tlw5 mgt10">
                                <tbody>
                                    <tr>
                                        <th className="left" width="140">Plant</th>
                                        <td className="left">
                                            <OwpSelectField
                                                className="w-full left"
                                                id="OWP_MBR.MBRPLANT"
                                                name="OWP_MBR.MBRPLANT"
                                                groupId={"D019000"}
                                                onChange={this.handleChange}
                                                isall={false}
                                                value={this.state['OWP_MBR.MBRPLANT']}
                                            />
                                        </td>
                                        <th className="left" width="140">Record</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRRECORD"
                                                name="OWP_MBR.MBRRECORD"
                                                value="Batch"
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">MBR No.</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRNO"
                                                name="OWP_MBR.MBRNO"
                                                value={this.state['OWP_MBR.MBRNO']}
                                            />
                                        </td>
                                        <th className="left" width="140">Version</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRVER"
                                                name="OWP_MBR.MBRVER"
                                                value={this.state['OWP_MBR.MBRVER']}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Product</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.PRODUCTNAME"
                                                name="OWP_MBR.PRODUCTNAME"
                                                value={this.state['OWP_MBR.PRODUCTNAME']}
                                            />
                                        </td>
                                        <th className="left" width="140">Product ID No.</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.PRODUCTID"
                                                name="OWP_MBR.PRODUCTID"
                                                value={this.state['OWP_MBR.PRODUCTID']}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Written by Production Engineer(Writer)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.USERNAME"
                                                name="OWP_MBR.USERNAME"
                                                value={this.state['OWP_MBR.USERNAME']}
                                            />
                                        </td>
                                        <th className="left" width="140">Writer Registration Date</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.WDATE"
                                                name="OWP_MBR.WDATE"
                                                value={this.state['OWP_MBR.WDATE'] ? moment(this.state['OWP_MBR.WDATE']).format(KOREAN_FORMAT_DATE)
                                                : ''}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Approved by Production Manager(Reviewer1)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRAPPRBYPM"
                                                name="OWP_MBR.MBRAPPRBYPM"
                                                value={this.state['OWP_MBR.APPRBYPM']}
                                            />
                                        </td>
                                        <th className="left" width="140">Approve Date(Reviewer1)</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRAPPRBYPMDATE"
                                                name="OWP_MBR.MBRAPPRBYPMDATE"
                                                value={this.state['OWP_MBR.MBRAPPRBYPMDATE'] ? moment(this.state['OWP_MBR.MBRAPPRBYPMDATE']).format(KOREAN_FORMAT_DATE)
                                                : ''}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Approved by QA Manager(Reviewer2)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRAPPRBYQM"
                                                name="OWP_MBR.MBRAPPRBYQM"
                                                value={this.state['OWP_MBR.APPRBYQA']}
                                            />
                                        </td>
                                        <th className="left" width="140">Approve Date(Reviewer2)</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRAPPRBYQMDATE"
                                                name="OWP_MBR.MBRAPPRBYQMDATE"
                                                value={this.state['OWP_MBR.MBRAPPRBYQMDATE'] ? moment(this.state['OWP_MBR.MBRAPPRBYQMDATE']).format(KOREAN_FORMAT_DATE)
                                                : ''}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Effective date</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBREFFDATE"
                                                name="OWP_MBR.MBREFFDATE"
                                                value={this.state['OWP_MBR.MBREFFDATE']}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                        <th className="left" width="140">Related Training Record Doc</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRTRAINDOC"
                                                name="OWP_MBR.MBRTRAINDOC"
                                                value={this.state['OWP_MBR.MBRTRAINDOC']}
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                            <div className="b mgt20">Batch Information</div>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                    <tr>
                                        <th className="left" width="140">Batch Record Issue Date</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.BRISSUEDATE"
                                                name="OWP_MBR.BRISSUEDATE"
                                                value={this.state['OWP_MBR.BRISSUEDATE']}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                        <th className="left" width="140">Theoretical Yield(kg)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.THEOREYIELD"
                                                name="OWP_MBR.THEOREYIELD"
                                                value={this.state['OWP_MBR.THEOREYIELD']}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Batch Record Issued By</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.BRISSUEDBY"
                                                name="OWP_MBR.BRISSUEDBY"
                                                value={this.state['OWP_MBR.BRISSUEDBY']}
                                            />
                                        </td>
                                        <th className="left" width="140">Expected Product Weight(kg)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.EXPPRODWT"
                                                name="OWP_MBR.EXPPRODWT"
                                                value={this.state['OWP_MBR.EXPPRODWT']}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Production Start Date</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.PRODSTARTDATE"
                                                name="OWP_MBR.PRODSTARTDATE"
                                                value={this.state['OWP_MBR.PRODSTARTDATE']}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                        <th className="left" width="140">Actual Product Weight(kg)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.ACTUALPRODWT"
                                                name="OWP_MBR.ACTUALPRODWT"
                                                value={this.state['OWP_MBR.ACTUALPRODWT']}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Production Completion Date</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.PRODCOMPDATE"
                                                name="OWP_MBR.PRODCOMPDATE"
                                                value={this.state['OWP_MBR.PRODCOMPDATE']}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                        <th className="left" width="140">Expected Percent Yield(mol%)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.EXTPERYIELD"
                                                name="OWP_MBR.EXTPERYIELD"
                                                value={this.state['OWP_MBR.EXTPERYIELD']}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">Actual Percent Yield(mol%)</th>
                                        <td className="left" colSpan={3}>
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.ACTUALPERYIELD"
                                                name="OWP_MBR.ACTUALPERYIELD"
                                                value={this.state['OWP_MBR.ACTUALPERYIELD']}
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                            <div className="b mgt20">Batch Record Approval</div>
                            <table className="table table-bordered table-advance tlw5">
                                <tbody>
                                    <tr>
                                        <th className="left" width="140">Production Manager
    (Signature)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRPMSIGNATURE"
                                                name="OWP_MBR.MBRPMSIGNATURE"
                                                value={this.state['OWP_MBR.MBRPMSIGNATURE']}
                                            />
                                        </td>
                                        <th className="left" width="140">Production Manager
    (Signature Date)</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRPMSIGNATUREDATE"
                                                name="OWP_MBR.MBRPMSIGNATUREDATE"
                                                value={this.state['OWP_MBR.MBRPMSIGNATUREDATE']}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                    </tr>
                                    <tr>
                                        <th className="left" width="140">QA Manager
    (Signature)</th>
                                        <td className="left">
                                            <OwpTextField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRQMSIGNATURE"
                                                name="OWP_MBR.MBRQMSIGNATURE"
                                                value={this.state['OWP_MBR.MBRQMSIGNATURE']}
                                            />
                                        </td>
                                        <th className="left" width="140">QA Manager
    (Signature Date)</th>
                                        <td className="left">
                                            <OwpSearchDateField
                                                style={{ width: '100%' }}
                                                id="OWP_MBR.MBRQMSIGNATUREDATE"
                                                name="OWP_MBR.MBRQMSIGNATUREDATE"
                                                value={this.state['OWP_MBR.MBRQMSIGNATUREDATE']}
                                                onChange={this.handleChange}
                                                initNow={true}
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </div>
                </Formsy>
            </React.Fragment>
        );
    }
}

export default Page;
