import React, { Component } from 'react';
import * as TGEvent from 'owp/TGEvent';
import {
    OwpPageCarded,
    OwpTreeGrid,
} from 'owp/wrapper';
import Icon from '@material-ui/core/Icon';
import Dialog_C803001 from 'main/content/include/Dialog_C803001';
import Dialog_C803002 from 'main/content/include/Dialog_C803002';
import Dialog_C803003 from 'main/content/include/Dialog_C803003';
import Dialog_C803004 from 'main/content/include/Dialog_C803004';
import Dialog_C803005 from 'main/content/include/Dialog_C803005';
import Dialog_C803006 from 'main/content/include/Dialog_C803006';
import { onGridLoad } from '../../../GridUtill';
import translationHelper from 'main/content/utils/translationHelper';
const pageID = 'C803000';

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
            [name]: value,
        });
    };

    async componentDidMount() {
        onGridLoad("C803001", () => {
            TGEvent.OWPTGDataList("C803001", "listIPX_CommonCodeHighAC", {
                'IPX_COMMONCODE.FLAG' : 'Y'
            })
        })

        TGEvent.OWPTGSelect("C803001", (is, row) => {
            //좌측그리드
            if (is) {
                this.setState({
                    CODEID: row["IPX_COMMONCODE.CODEID"],
                    CODENM: row["IPX_COMMONCODE.CODENM"],
                    GROUPID: row["IPX_COMMONCODE.GROUPID"],
                    GROUPNM: row["IPX_COMMONCODE.GROUPNM"],
                    FLAG: row["IPX_COMMONCODE.FLAG"], // 하위코드명
                })
                TGEvent.OWPTGDataList("C803002", "listIPX_CommonCodeLowAC", {
                    "IPX_COMMONCODE.GROUPID": row["IPX_COMMONCODE.CODEID"]
                })
            } else {
                this.setState({
                    CODEID: null,
                    CODENM: null,
                    GROUPID: null,
                    GROUPNM: null
                })
                TGEvent.OWPTGDataList("C803002", "listIPX_CommonCodeLowAC", {
                    "IPX_COMMONCODE.GROUPID": "XXXXXXX"
                })
            }
        })

        TGEvent.OWPTGSelect("C803002", (is, row) => {
            //좌측 그리드
            if (is) {
                this.setState({
                    LOWCODEID: row["IPX_COMMONCODE.CODEID"], // 하위코드 ID
                    LOWCODENM: row["IPX_COMMONCODE.CODENM"], // 하위코드명
                    FLAG: row["IPX_COMMONCODE.FLAG"], // 하위코드명
                })
            } else {
                this.setState({
                    LOWCODEID: null,
                    LOWCODENM: null
                })
            }
        })
    }

    // 상위코드 목록 호출
    fetchlistPermission = () => {

        let interval = window.setInterval(() => {
            let G = TGEvent.getGridByID("C993001");
            if (!!G) {
                if (G.Loading === 0) {
                    window.clearInterval(interval);
                    G.Data.Data.Url =
                        process.env.REACT_APP_REST_API_URL + "/" + encodeURI('listIPX_CommonCodeHighAC?jsondata={"VER":"2","CODEID":"C903000"}')
                    G.ReloadBody();
                    G.ClearSelection();

                }
            }
        }, 100)
    };

    // 하위코드 목록 호출
    fetchlistIpxPagepermissionData = (condition) => {
        const G = TGEvent.getGridByID("C993002")
        G.Data.Data.Url =
            process.env.REACT_APP_REST_API_URL + "/" + encodeURI('listIpxCommoncode?jsondata={"IPX_COMMONCODE.GROUPID":"' + condition + '","VER":"2"}')
        G.ReloadBody();
        G.ClearSelection();
    };

    componentWillUnmount() {
        TGEvent.OWPTGDelEvent();
    }

    // CODEID, CODENM, GROUPID, GROUPNM 초기화
    ClearProps = () => {
        this.setState({
            CODEID: null,
            CODENM: null,
            GROUPID: null,
            GROUPNM: null,
            FLAG: null,
        });
    };

    // LOW_CODEID, LOW_CODENM, GROUPID, GROUPNM 초기화
    lowClearProps = () => {
        this.setState({
            LOWCODEID: null,
            LOWCODENM: null,
            GROUPID: null,
            GROUPNM: null,
            FLAG: null,
        });
    };

    render() {
        return (
            <OwpPageCarded
                content={
                    <React.Fragment>
                        <div className="p-12">
                            <div className="col-lg-5">
                                <div className="portlet light bordered">
                                    <div className="portlet-title">
                                        <div className="caption">
                                            <Icon className="ico_m01 c04">insert_chart</Icon>
                                            <span className="caption-subject font-dark bold uppercase">{translationHelper(pageID + '.title.top_code_list')}</span> {/*Upper Code List*/}
                                            <span className="caption-helper"> </span>
                                        </div>
                                        <div className="flex justify-end">
                                            <Dialog_C803001 /> {/*상위코드 등록*/}
                                            <div id="updateIPX_CommonCodeHigh">
                                                <Dialog_C803002
                                                    CODEID={this.state.CODEID}
                                                    ClearProps={this.ClearProps}
                                                />
                                            </div>
                                            {/*상위코드 수정*/}
                                            <div id="deleteIPX_CommonCodeHigh">
                                                {/*하위코드 삭제*/}
                                                <Dialog_C803003
                                                    CODEID={this.state.CODEID}
                                                    ClearProps={this.ClearProps}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="portlet-body">
                                        <OwpTreeGrid
                                            id={"C803001"}
                                            height={"73vh"}
                                            LayoutUrl={"assets/data/C803001Def.xml"}
                                        />
                                    </div>
                                </div>
                            </div>
                            <div className="col-lg-7">
                                <div className="portlet light bordered">
                                    <div className="portlet-title">
                                        <div className="caption">
                                            <Icon className="ico_m01 c04">insert_chart</Icon>
                                            <span className="caption-subject font-dark bold uppercase">{translationHelper(pageID + '.title.sub_code_list')}</span> {/*Sub Code List*/}
                                            <span className="caption-helper"> </span>
                                        </div>
                                        <div className="flex justify-end">
                                            <div id="createIPX_CommonCodeLow">
                                                {/*하위코드 등록*/}
                                                <Dialog_C803004
                                                    GROUPID={this.state.GROUPID}
                                                    GROUPNM={this.state.GROUPNM}
                                                    CODEID={this.state.CODEID}
                                                    CODENM={this.state.CODENM}
                                                    LowClearProps={this.lowClearProps}
                                                />
                                            </div>
                                            <div id="updateIPX_CommonCodeLow">
                                                {/*하위코드 수정*/}
                                                <Dialog_C803005
                                                    LOWCODEID={this.state.LOWCODEID}
                                                    LOWCODENM={this.state.LOWCODENM}
                                                    LowClearProps={this.lowClearProps}
                                                />
                                            </div>
                                            <div id="deleteIPX_CommonCodeLow">
                                                {/*하위코드 삭제*/}
                                                <Dialog_C803006
                                                    LOWCODEID={this.state.LOWCODEID}
                                                    LOWCODENM={this.state.LOWCODENM}
                                                    LowClearProps={this.lowClearProps}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                    <div className="portlet-body">
                                        <OwpTreeGrid
                                            id={"C803002"}
                                            height={"73vh"}
                                            LayoutUrl={"assets/data/C803002Def.xml"}
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </React.Fragment>
                }
                innerScroll
            />
        );
    }
}

export default Page;
